                                                                                                                                                function get_string_between($string, $start, $end) {
                                                                                                                                                    $start = preg_quote($start);
                                                                                                                                                    $end = preg_quote($end);
                                                                                                                                                    $pattern = "~$start\s*(.*?)$end\s*~";
                                                                                                                                                    $match = preg_match_all($pattern, $string, $matches);
                                                                                                                                                    if ($match) {
                                                                                                                                                        return $matches[1];
                                                                                                                                                    }
                                                                                                                                                }

                                                                                                                                                $parsed1 = preg_replace('/\s+/', '', get_string_between($fullstring, '[link]', '[/link]'));
