                                                                                                                                                } else {
                                                                                                                                                    $max = (100 / count($parsed1));
                                                                                                                                                }

                                                                                                                                                if (strpos($fullstring, '[cta]') !== false and strpos($fullstring, '[/cta]') !== false) {

                                                                                                                                                    for ($i=0; $i < count($parsed1); $i++) {
